import { useState } from 'react';
import { tools, categories } from '@/data/tools';
import { Filter, ChevronUp, ChevronDown, RotateCcw } from 'lucide-react';

interface SearchFiltersProps {
  onFiltersChange: (filters: SearchFilters) => void;
  className?: string;
}

export interface SearchFilters {
  category: string;
  sortBy: 'name' | 'category' | 'popularity';
  showPopularOnly: boolean;
  showFavoritesOnly: boolean;
}

const EnhancedSearchFilters = ({ onFiltersChange, className = '' }: SearchFiltersProps) => {
  const [filters, setFilters] = useState<SearchFilters>({
    category: 'all',
    sortBy: 'name',
    showPopularOnly: false,
    showFavoritesOnly: false,
  });

  const [isExpanded, setIsExpanded] = useState(false);

  const handleFilterChange = (newFilters: Partial<SearchFilters>) => {
    const updated = { ...filters, ...newFilters };
    setFilters(updated);
    onFiltersChange(updated);
  };

  const resetFilters = () => {
    const defaultFilters: SearchFilters = {
      category: 'all',
      sortBy: 'name',
      showPopularOnly: false,
      showFavoritesOnly: false,
    };
    setFilters(defaultFilters);
    onFiltersChange(defaultFilters);
  };

  const categoryStats = Object.keys(categories).reduce((acc, key) => {
    acc[key] = tools.filter(tool => tool.category === key).length;
    return acc;
  }, {} as Record<string, number>);

  const hasActiveFilters = filters.category !== 'all' || 
                          filters.sortBy !== 'name' || 
                          filters.showPopularOnly || 
                          filters.showFavoritesOnly;

  return (
    <div className={`bg-white dark:bg-neutral-800 rounded-lg shadow-sm border border-gray-200 dark:border-neutral-700 ${className}`}>
      <div className="p-4 border-b border-gray-200 dark:border-neutral-700">
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="flex items-center justify-between w-full text-left"
          data-testid="button-toggle-filters"
        >
          <div className="flex items-center space-x-2">
            <Filter className="text-secondary dark:text-secondary" size={16} />
            <span className="font-medium text-neutral-800 dark:text-neutral-100">
              Advanced Filters
            </span>
            {hasActiveFilters && (
              <span className="bg-secondary/20 dark:bg-secondary/20 text-secondary dark:text-secondary text-xs px-2 py-1 rounded-full">
                Active
              </span>
            )}
          </div>
          {isExpanded ? <ChevronUp className="text-neutral-400" size={16} /> : <ChevronDown className="text-neutral-400" size={16} />}
        </button>
      </div>

      {isExpanded && (
        <div className="p-4 space-y-4">
          {/* Category Filter */}
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-2">
              Category
            </label>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange({ category: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 dark:border-neutral-600 bg-white dark:bg-neutral-700 text-neutral-900 dark:text-neutral-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-secondary"
              data-testid="select-category-filter"
            >
              <option value="all">All Categories ({tools.length})</option>
              {Object.entries(categories).map(([key, label]) => (
                <option key={key} value={key}>
                  {label} ({categoryStats[key]})
                </option>
              ))}
            </select>
          </div>

          {/* Sort Options */}
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-2">
              Sort By
            </label>
            <select
              value={filters.sortBy}
              onChange={(e) => handleFilterChange({ sortBy: e.target.value as any })}
              className="w-full px-3 py-2 border border-gray-300 dark:border-neutral-600 bg-white dark:bg-neutral-700 text-neutral-900 dark:text-neutral-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-secondary"
              data-testid="select-sort-filter"
            >
              <option value="name">Name (A-Z)</option>
              <option value="category">Category</option>
              <option value="popularity">Popularity</option>
            </select>
          </div>

          {/* Quick Filters */}
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-2">
              Quick Filters
            </label>
            <div className="space-y-2">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={filters.showPopularOnly}
                  onChange={(e) => handleFilterChange({ showPopularOnly: e.target.checked })}
                  className="rounded border-gray-300 text-secondary focus:ring-secondary"
                  data-testid="checkbox-popular-only"
                />
                <span className="ml-2 text-sm text-neutral-700 dark:text-neutral-300">
                  Show popular tools only
                </span>
              </label>
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={filters.showFavoritesOnly}
                  onChange={(e) => handleFilterChange({ showFavoritesOnly: e.target.checked })}
                  className="rounded border-gray-300 text-secondary focus:ring-secondary"
                  data-testid="checkbox-favorites-only"
                />
                <span className="ml-2 text-sm text-neutral-700 dark:text-neutral-300">
                  Show favorite tools only
                </span>
              </label>
            </div>
          </div>

          {/* Reset Button */}
          {hasActiveFilters && (
            <div className="pt-2 border-t border-gray-200 dark:border-neutral-600">
              <button
                onClick={resetFilters}
                className="w-full px-4 py-2 text-sm text-neutral-600 dark:text-neutral-400 hover:text-neutral-800 dark:hover:text-neutral-200 border border-gray-300 dark:border-neutral-600 rounded-lg hover:bg-gray-50 dark:hover:bg-neutral-700 transition-colors"
                data-testid="button-reset-filters"
              >
                <RotateCcw className="mr-2" size={16} />
                Reset Filters
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default EnhancedSearchFilters;

client/src/components/FavoriteButton.tsx.content:
import { useFavorites } from '@/hooks/use-favorites';
import { Tool } from '@/data/tools';
import { Star } from 'lucide-react';

interface FavoriteButtonProps {
  tool: Tool;
  className?: string;
  showLabel?: boolean;
  size?: 'sm' | 'md' | 'lg';
}

const FavoriteButton = ({ 
  tool, 
  className = '', 
  showLabel = false, 
  size = 'md' 
}: FavoriteButtonProps) => {
  const { isFavorite, toggleFavorite } = useFavorites();
  const isToolFavorite = isFavorite(tool.id);

  const sizeClasses = {
    sm: 'p-1 text-sm',
    md: 'p-2 text-base',
    lg: 'p-3 text-lg'
  };

  const iconSizes = {
    sm: 14,
    md: 16,
    lg: 20
  };

  return (
    <button
      onClick={(e) => {
        e.preventDefault();
        e.stopPropagation();
        toggleFavorite(tool);
      }}
      className={`
        ${sizeClasses[size]} 
        ${isToolFavorite 
          ? 'text-accent hover:text-accent/80' 
          : 'text-neutral-400 hover:text-accent'
        } 
        transition-colors duration-200 
        ${className}
      `}
      title={isToolFavorite ? 'Remove from favorites' : 'Add to favorites'}
      data-testid={`button-favorite-${tool.id}`}
    >
      <Star 
        size={iconSizes[size]} 
        className={isToolFavorite ? 'fill-current' : ''}
      />
      {showLabel && (
        <span className="ml-2 text-xs font-medium">
          {isToolFavorite ? 'Favorited' : 'Add to Favorites'}
        </span>
      )}
    </button>
  );
};

export default FavoriteButton;

client/src/components/FavoritesDropdown..content:
import { useState, useRef, useEffect } from 'react';
import { Link } from 'wouter';
import { useFavorites } from '@/hooks/use-favorites';

const FavoritesDropdown = () => {
  const [isOpen, setIsOpen] = useState(false);
  const { favorites } = useFavorites();
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-2 text-neutral-600 dark:text-neutral-300 hover:text-blue-500 dark:hover:text-blue-400 transition-colors relative"
        data-testid="button-favorites"
        title="Favorites"
      >
        <i className="fas fa-star text-lg"></i>
        {favorites.length > 0 && (
          <span className="absolute -top-1 -right-1 bg-blue-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center font-medium">
            {favorites.length > 9 ? '9+' : favorites.length}
          </span>
        )}
      </button>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-white dark:bg-neutral-800 rounded-lg shadow-xl border border-gray-200 dark:border-neutral-700 z-50 max-h-96 overflow-hidden">
          <div className="p-4 border-b border-gray-200 dark:border-neutral-700">
            <h3 className="font-semibold text-neutral-800 dark:text-neutral-100 flex items-center">
              <i className="fas fa-star text-yellow-500 mr-2"></i>
              Favorite Tools
            </h3>
          </div>

          <div className="max-h-80 overflow-y-auto">
            {favorites.length > 0 ? (
              favorites.map((tool) => (
                <Link
                  key={tool.id}
                  href={tool.href}
                  className="block p-3 hover:bg-gray-50 dark:hover:bg-neutral-700 border-b border-gray-100 dark:border-neutral-600 last:border-b-0 transition-colors"
                  onClick={() => setIsOpen(false)}
                  data-testid={`favorite-tool-${tool.id}`}
                >
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center flex-shrink-0">
                      <i className={`${tool.icon} text-white text-xs`}></i>
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="font-medium text-neutral-800 dark:text-neutral-100 text-sm truncate">
                        {tool.name}
                      </div>
                      <div className="text-xs text-neutral-500 dark:text-neutral-400 truncate">
                        {tool.description}
                      </div>
                    </div>
                  </div>
                </Link>
              ))
            ) : (
              <div className="p-8 text-center text-neutral-500 dark:text-neutral-400">
                <i className="fas fa-star text-3xl mb-3 text-neutral-300 dark:text-neutral-600"></i>
                <p className="text-sm">No favorite tools yet</p>
                <p className="text-xs mt-1">Click the star icon on any tool to add it here</p>
              </div>
            )}
          </div>

          {favorites.length > 0 && (
            <div className="p-3 border-t border-gray-200 dark:border-neutral-700 bg-gray-50 dark:bg-neutral-750">
              <Link
                href="/tools"
                className="text-xs text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 font-medium"
                onClick={() => setIsOpen(false)}
                data-testid="link-view-all-tools"
              >
                View all tools →
              </Link>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default FavoritesDropdown;

