import { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { useIsMobile } from '@/hooks/use-mobile';
import { searchTools } from '@/lib/search';
import { tools } from '@/data/tools';
import Logo from './Logo';
import { Menu, X, Search } from 'lucide-react';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(tools);
  const [location, setLocation] = useLocation();
  const isMobile = useIsMobile();

  const handleSearch = (query: string) => {
    setSearchQuery(query);
    const results = searchTools(query);
    setSearchResults(results);
  };

  const handleToolClick = (toolHref: string) => {
    setIsSearchOpen(false);
    setSearchQuery('');
    setLocation(toolHref);
    setIsMobileMenuOpen(false);
  };

  const handleLinkClick = () => {
    setIsMobileMenuOpen(false);
  };

  const navLinks = [
    { href: '/math-games', label: 'Math Games' },
    { href: '/science-games', label: 'Science Games' },
    { href: '/language-games', label: 'Language Games' },
    { href: '/memory-games', label: 'Memory Games' },
    { href: '/logic-games', label: 'Logic Games' }
  ];

  return (
    <header className="sticky top-0 z-50 bg-white dark:bg-neutral-900 shadow-sm border-b border-gray-200 dark:border-neutral-700 transition-colors duration-200" data-testid="header-main">
      <div className="max-w-7xl mx-auto px-3 sm:px-4 lg:px-8">
        <div className="flex justify-between items-center h-14 sm:h-16">
          {/* Logo Section */}
          <Link href="/" className="flex items-center flex-shrink-0" data-testid="link-home" onClick={handleLinkClick}>
            <Logo className="h-7 w-7 sm:h-8 sm:w-8 mr-2 sm:mr-3" />
            <span className="text-lg sm:text-xl font-bold truncate" data-testid="text-site-name">
              <span className="text-neutral-800 dark:text-neutral-100">Dapsi</span>
              <span className="text-primary"> Games</span>
            </span>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden lg:flex items-center space-x-6 xl:space-x-8">
            {navLinks.map((link) => (
              <Link
                key={link.href}
                href={link.href}
                className={`text-neutral-600 dark:text-neutral-300 hover:text-primary dark:hover:text-primary transition-colors duration-200 font-medium whitespace-nowrap ${
                  location === link.href ? 'text-primary dark:text-primary' : ''
                }`}
                data-testid={`link-${link.label.toLowerCase().replace(' ', '-')}`}
              >
                {link.label}
              </Link>
            ))}
          </nav>

          {/* Search and Mobile Menu */}
          <div className="flex items-center space-x-1 sm:space-x-2">
            {/* Search */}
            <button
              className="p-2 text-neutral-600 dark:text-neutral-300 hover:text-primary dark:hover:text-primary transition-colors rounded-lg hover:bg-neutral-100 dark:hover:bg-neutral-800"
              onClick={() => setIsSearchOpen(true)}
              data-testid="button-search"
              aria-label="Search tools"
              title="Search tools"
            >
              <Search size={isMobile ? 16 : 18} />
            </button>

            {/* Mobile Menu */}
            <button
              className="lg:hidden p-2 text-neutral-600 dark:text-neutral-300 hover:text-primary dark:hover:text-primary transition-colors rounded-lg hover:bg-neutral-100 dark:hover:bg-neutral-800"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              data-testid="button-mobile-menu"
              aria-label={isMobileMenuOpen ? "Close mobile menu" : "Open mobile menu"}
              aria-expanded={isMobileMenuOpen}
            >
              {isMobileMenuOpen ? <X size={isMobile ? 16 : 18} /> : <Menu size={isMobile ? 16 : 18} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div className="lg:hidden fixed inset-0 top-14 sm:top-16 z-40">
          {/* Backdrop */}
          <div 
            className="absolute inset-0 bg-black bg-opacity-50"
            onClick={() => setIsMobileMenuOpen(false)}
          />

          {/* Menu Content */}
          <nav
            className="relative bg-white dark:bg-neutral-900 border-t border-gray-200 dark:border-neutral-700 shadow-lg max-h-screen overflow-y-auto"
            data-testid="mobile-menu"
            aria-label="Mobile navigation"
          >
            <div className="px-4 py-6 space-y-1">
              {navLinks.map((link) => (
                <Link
                  key={link.href}
                  href={link.href}
                  className={`block px-4 py-3 text-base font-medium transition-colors rounded-lg ${
                    location === link.href 
                      ? 'text-primary dark:text-primary bg-red-50 dark:bg-red-900/20' 
                      : 'text-neutral-600 dark:text-neutral-300 hover:text-primary dark:hover:text-primary hover:bg-neutral-50 dark:hover:bg-neutral-800'
                  }`}
                  onClick={handleLinkClick}
                  data-testid={`mobile-link-${link.label.toLowerCase().replace(' ', '-')}`}
                >
                  {link.label}
                </Link>
              ))}
            </div>
          </nav>
        </div>
      )}

      {/* Search Modal */}
      {isSearchOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-start justify-center pt-4 sm:pt-20 px-4">
          <div
            className="bg-white dark:bg-neutral-900 rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] sm:max-h-96 overflow-hidden"
            role="dialog"
            aria-label="Search tools"
            aria-modal="true"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="p-3 sm:p-4 border-b border-gray-200 dark:border-neutral-700">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search for study games..."
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="w-full py-2 sm:py-3 px-3 sm:px-4 pr-10 sm:pr-12 text-base sm:text-lg border border-gray-300 dark:border-neutral-600 bg-white dark:bg-neutral-800 text-neutral-900 dark:text-neutral-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-secondary dark:focus:ring-secondary"
                  autoFocus
                  data-testid="search-modal-input"
                  aria-label="Search for tools"
                />
                <button
                  onClick={() => setIsSearchOpen(false)}
                  className="absolute right-2 sm:right-3 top-2 sm:top-3 text-gray-400 dark:text-neutral-500 hover:text-gray-600 dark:hover:text-neutral-300 p-1 rounded-md hover:bg-gray-100 dark:hover:bg-neutral-700 transition-colors"
                  data-testid="search-modal-close"
                  aria-label="Close search"
                >
                  <X size={isMobile ? 16 : 18} />
                </button>
              </div>
            </div>
            <div className="max-h-[60vh] sm:max-h-80 overflow-y-auto">
              {searchResults.length > 0 ? (
                searchResults.slice(0, 10).map((tool) => (
                  <button
                    key={tool.id}
                    onClick={() => handleToolClick(tool.href)}
                    className="w-full p-3 sm:p-4 text-left hover:bg-gray-50 dark:hover:bg-neutral-800 border-b border-gray-100 dark:border-neutral-700 transition-colors"
                    data-testid={`search-result-${tool.id}`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="flex-1 min-w-0">
                        <div className="font-medium text-gray-900 dark:text-neutral-100 truncate text-sm sm:text-base">{tool.name}</div>
                        <div className="text-xs sm:text-sm text-gray-500 dark:text-neutral-400 truncate">{tool.description}</div>
                      </div>
                      {tool.isPopular && (
                        <div className="bg-yellow-100 text-yellow-800 text-xs px-2 py-1 rounded-full flex-shrink-0">
                          Popular
                        </div>
                      )}
                    </div>
                  </button>
                ))
              ) : (
                <div className="p-6 sm:p-8 text-center text-gray-500 dark:text-neutral-400">
                  <p className="text-sm sm:text-base">No tools found matching "{searchQuery}"</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;

client/src/components/HeroSection.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { searchTools } from '@/lib/search';
import { tools } from '@/data/tools';
import { Search, Play, Sparkles, Trophy, Target } from 'lucide-react';

const HeroSection = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchResults, setSearchResults] = useState(tools.slice(0, 8));
  const [, setLocation] = useLocation();

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (query.trim()) {
      const results = searchTools(query);
      setSearchResults(results.slice(0, 8));
      setIsSearchOpen(true);
    } else {
      setSearchResults(tools.slice(0, 8));
      setIsSearchOpen(false);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setLocation(`/games?search=${encodeURIComponent(searchQuery.trim())}`);
    } else {
      setLocation('/games');
    }
    setIsSearchOpen(false);
  };

  const handleToolClick = (toolHref: string) => {
    setIsSearchOpen(false);
    setSearchQuery('');
    setLocation(toolHref);
  };

  const handleInputFocus = () => {
    if (searchResults.length > 0) {
      setIsSearchOpen(true);
    }
  };

  const handleInputBlur = () => {
    // Delay hiding to allow clicks on results
    setTimeout(() => setIsSearchOpen(false), 200);
  };

  return (
    <section className="gradient-gaming relative overflow-hidden" data-testid="hero-section">
      {/* Floating gaming elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none" aria-hidden="true" role="presentation">
        <div className="absolute top-20 left-20 text-yellow-300/20 text-6xl motion-safe:animate-bounce">🎮</div>
        <div className="absolute top-40 right-32 text-blue-300/20 text-4xl motion-safe:animate-pulse">⭐</div>
        <div className="absolute bottom-32 left-16 text-green-300/20 text-5xl motion-safe:animate-bounce motion-safe:delay-500">🏆</div>
        <div className="absolute bottom-20 right-20 text-yellow-300/20 text-3xl motion-safe:animate-pulse motion-safe:delay-300">💡</div>
        <div className="absolute top-60 left-1/3 text-blue-300/20 text-4xl motion-safe:animate-bounce motion-safe:delay-700">🧠</div>
      </div>

      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20 lg:py-32">

        {/* Hero Content */}
        <div className="text-center text-white">
          {/* Logo and Brand */}
          <div className="flex justify-center items-center mb-8">
            <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-4 shadow-2xl border border-white/20">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center">
                  <span className="text-2xl">🎓</span>
                </div>
                <div className="text-left">
                  <h2 className="text-2xl font-bold text-white">DapsiGames</h2>
                  <p className="text-white/80 text-sm">Study + Gaming Hub</p>
                </div>
              </div>
            </div>
          </div>

          <h1 className="text-4xl sm:text-5xl lg:text-7xl font-bold mb-6 leading-tight" data-testid="text-hero-title">
            <span className="bg-gradient-to-r from-yellow-300 to-yellow-100 bg-clip-text text-transparent">
              Learn Through Play
            </span>
            <br />
            <span className="text-white">150+ Study Games</span>
          </h1>

          <p className="text-xl lg:text-2xl text-white/90 mb-12 max-w-4xl mx-auto leading-relaxed" data-testid="text-hero-subtitle">
            Transform studying into an adventure! Master math, science, language, memory, and logic through 
            <span className="text-yellow-300 font-semibold"> engaging educational games</span> designed to make learning fun and effective.
          </p>

          {/* Call-to-Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16">
            <button
              onClick={() => setLocation('/games')}
              className="group bg-yellow-400 hover:bg-yellow-300 text-gray-900 px-8 py-4 rounded-2xl font-bold text-lg shadow-2xl hover:shadow-yellow-400/25 transition-all duration-300 transform hover:-translate-y-1 hover:scale-105 flex items-center gap-3"
              data-testid="button-start-playing"
            >
              <Play className="w-6 h-6" />
              Start Playing Now
              <Sparkles className="w-5 h-5 group-hover:animate-spin" />
            </button>

            <button
              onClick={() => setLocation('/about')}
              className="bg-white/10 hover:bg-white/20 backdrop-blur-sm text-white border-2 border-white/30 px-8 py-4 rounded-2xl font-semibold text-lg transition-all duration-300 hover:border-white/50"
              data-testid="button-learn-more"
            >
              Learn More
            </button>
          </div>

          {/* Search Bar */}
          <div className="max-w-2xl mx-auto mb-16 relative">
          <form onSubmit={handleSearch} className="relative">
            <input 
              type="text" 
              placeholder="Search for study games..."
              value={searchQuery}
              onChange={handleSearchChange}
              onFocus={handleInputFocus}
              onBlur={handleInputBlur}
              className="w-full py-4 px-6 pr-16 text-lg text-neutral-800 bg-white rounded-2xl shadow-lg focus:outline-none focus:ring-4 focus:ring-secondary/30 transition-all duration-200"
              data-testid="input-search-tools"
            />
            <button 
              type="submit"
              className="absolute right-2 top-2 bottom-2 px-6 bg-primary text-white rounded-xl hover:bg-primary/90 transition-all duration-200 shadow-md hover:shadow-lg"
              data-testid="button-search-tools"
            >
              <Search size={20} />
            </button>
          </form>

          {/* Search Results Dropdown */}
          {isSearchOpen && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-white rounded-2xl shadow-xl border border-gray-200 z-50 max-h-96 overflow-hidden">
              {searchResults.length > 0 ? (
                <div className="py-2">
                  {searchResults.map((tool) => (
                    <button
                      key={tool.id}
                      onClick={() => handleToolClick(tool.href)}
                      className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors border-b border-gray-100 last:border-b-0"
                      data-testid={`hero-search-result-${tool.id}`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex-1 min-w-0">
                          <div className="font-medium text-gray-900 truncate">{tool.name}</div>
                          <div className="text-sm text-gray-500 truncate">{tool.description}</div>
                        </div>
                        {tool.isPopular && (
                          <div className="bg-accent/20 text-accent-foreground text-xs px-2 py-1 rounded-full flex-shrink-0">
                            Popular
                          </div>
                        )}
                      </div>
                    </button>
                  ))}

                  {searchQuery.trim() && (
                    <div className="px-4 py-3 border-t border-gray-200">
                      <button
                        onClick={handleSearch}
                        className="w-full text-center text-secondary hover:text-secondary/80 font-medium text-sm"
                        data-testid="hero-search-view-all"
                      >
                        View all results for "{searchQuery}" →
                      </button>
                    </div>
                  )}
                </div>
              ) : searchQuery.trim() ? (
                <div className="p-8 text-center text-neutral-500">
                  <Search size={48} className="mx-auto mb-4" />
                  <p>No tools found matching "{searchQuery}"</p>
                  <button
                    onClick={() => setLocation('/games')}
                    className="mt-4 text-secondary hover:text-secondary/80 font-medium"
                  >
                    Browse all games →
                  </button>
                </div>
              ) : null}
            </div>
