import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

client/src/lib/search.ts.content:
import Fuse from 'fuse.js';
import { tools, type Tool } from '@/data/tools';

const fuseOptions = {
  keys: [
    'name',
    'description',
    'category'
  ],
  threshold: 0.3,
  includeScore: true
};

const fuse = new Fuse(tools, fuseOptions);

export const searchTools = (query: string): Tool[] => {
  if (!query.trim()) return tools;

  const results = fuse.search(query);
  return results.map(result => result.item);
};

export const filterToolsByCategory = (category: string): Tool[] => {
  if (category === 'all') return tools;
  return tools.filter(tool => tool.category === category);
};

export const searchAndFilterTools = (query: string, category: string): Tool[] => {
  let filteredTools = filterToolsByCategory(category);

  if (!query.trim()) return filteredTools;

  const searchFuse = new Fuse(filteredTools, fuseOptions);
  const results = searchFuse.search(query);
  return results.map(result => result.item);
};

client/src/lib/userPreferences.ts.content:
import { Tool } from '@/data/tools';

// Local storage keys
const FAVORITES_KEY = 'dapsigames-favorites';
const RECENT_TOOLS_KEY = 'dapsigames-recent';
const USER_PREFERENCES_KEY = 'dapsigames-preferences';

export interface RecentTool {
  tool: Tool;
  timestamp: number;
}

export interface UserPreferences {
  favoriteCalculationPresets?: Record<string, any>;
  preferredTheme?: 'light' | 'dark' | 'system';
  showRecentTools?: boolean;
  maxRecentTools?: number;
}

// Data migration for localStorage keys (one-time migration from DapsiWow to DapsiGames)
const migrateOldData = () => {
  try {
    // Migration keys
    const OLD_FAVORITES_KEY = 'dapsiwow-favorites';
    const OLD_RECENT_TOOLS_KEY = 'dapsiwow-recent';
    const OLD_USER_PREFERENCES_KEY = 'dapsiwow-preferences';

    // Migrate favorites
    const oldFavorites = localStorage.getItem(OLD_FAVORITES_KEY);
    if (oldFavorites && !localStorage.getItem(FAVORITES_KEY)) {
      localStorage.setItem(FAVORITES_KEY, oldFavorites);
      localStorage.removeItem(OLD_FAVORITES_KEY);
    }

    // Migrate recent tools
    const oldRecent = localStorage.getItem(OLD_RECENT_TOOLS_KEY);
    if (oldRecent && !localStorage.getItem(RECENT_TOOLS_KEY)) {
      localStorage.setItem(RECENT_TOOLS_KEY, oldRecent);
      localStorage.removeItem(OLD_RECENT_TOOLS_KEY);
    }

    // Migrate user preferences
    const oldPrefs = localStorage.getItem(OLD_USER_PREFERENCES_KEY);
    if (oldPrefs && !localStorage.getItem(USER_PREFERENCES_KEY)) {
      localStorage.setItem(USER_PREFERENCES_KEY, oldPrefs);
      localStorage.removeItem(OLD_USER_PREFERENCES_KEY);
    }
  } catch (error) {
    console.error('Failed to migrate old localStorage data:', error);
  }
};

// Run migration on module load
migrateOldData();

// Favorites Management
export const getFavorites = (): Tool[] => {
  try {
    const stored = localStorage.getItem(FAVORITES_KEY);
    return stored ? JSON.parse(stored) : [];
  } catch {
    return [];
  }
};

export const addToFavorites = (tool: Tool): void => {
  try {
    const favorites = getFavorites();
    if (!favorites.some(fav => fav.id === tool.id)) {
      const updated = [...favorites, tool];
      localStorage.setItem(FAVORITES_KEY, JSON.stringify(updated));

      // Dispatch custom event for UI updates
      window.dispatchEvent(new CustomEvent('favoritesChanged', { 
        detail: { favorites: updated, action: 'add', tool } 
      }));
    }
  } catch (error) {
    console.error('Failed to add to favorites:', error);
  }
};

export const removeFromFavorites = (toolId: string): void => {
  try {
    const favorites = getFavorites();
    const updated = favorites.filter(tool => tool.id !== toolId);
    localStorage.setItem(FAVORITES_KEY, JSON.stringify(updated));

    // Dispatch custom event for UI updates
    window.dispatchEvent(new CustomEvent('favoritesChanged', { 
      detail: { favorites: updated, action: 'remove', toolId } 
    }));
  } catch (error) {
    console.error('Failed to remove from favorites:', error);
  }
};

export const isFavorite = (toolId: string): boolean => {
  return getFavorites().some(tool => tool.id === toolId);
};

// Recent Tools Management
export const getRecentTools = (): RecentTool[] => {
  try {
    const stored = localStorage.getItem(RECENT_TOOLS_KEY);
    const recent = stored ? JSON.parse(stored) : [];

    // Sort by timestamp (most recent first) and limit to prevent memory issues
    return recent
      .sort((a: RecentTool, b: RecentTool) => b.timestamp - a.timestamp)
      .slice(0, 20);
  } catch {
    return [];
  }
};

export const addToRecentTools = (tool: Tool): void => {
  try {
    const recent = getRecentTools();

    // Remove existing entry for this tool
    const filtered = recent.filter(item => item.tool.id !== tool.id);

    // Add new entry at the beginning
    const updated = [{ tool, timestamp: Date.now() }, ...filtered].slice(0, 15);

    localStorage.setItem(RECENT_TOOLS_KEY, JSON.stringify(updated));

    // Dispatch custom event for UI updates
    window.dispatchEvent(new CustomEvent('recentToolsChanged', { 
      detail: { recentTools: updated, tool } 
    }));
  } catch (error) {
    console.error('Failed to add to recent tools:', error);
  }
};

export const clearRecentTools = (): void => {
  try {
    localStorage.removeItem(RECENT_TOOLS_KEY);
    window.dispatchEvent(new CustomEvent('recentToolsChanged', { 
      detail: { recentTools: [], action: 'clear' } 
    }));
  } catch (error) {
    console.error('Failed to clear recent tools:', error);
  }
};

// User Preferences Management
export const getUserPreferences = (): UserPreferences => {
  try {
    const stored = localStorage.getItem(USER_PREFERENCES_KEY);
    return stored ? JSON.parse(stored) : {
      showRecentTools: true,
      maxRecentTools: 10
    };
  } catch {
    return {
      showRecentTools: true,
      maxRecentTools: 10
    };
  }
};

export const updateUserPreferences = (preferences: Partial<UserPreferences>): void => {
  try {
    const current = getUserPreferences();
    const updated = { ...current, ...preferences };
    localStorage.setItem(USER_PREFERENCES_KEY, JSON.stringify(updated));

    window.dispatchEvent(new CustomEvent('userPreferencesChanged', { 
      detail: { preferences: updated } 
    }));
  } catch (error) {
    console.error('Failed to update user preferences:', error);
  }
};

// Export utilities for sharing calculation results
export const generateShareableLink = (toolId: string, params: Record<string, any>): string => {
  const baseUrl = window.location.origin;
  const toolUrl = `${baseUrl}/tools/${toolId}`;

  // Encode parameters for URL
  const searchParams = new URLSearchParams();
  Object.entries(params).forEach(([key, value]) => {
    if (value !== null && value !== undefined && value !== '') {
      searchParams.append(key, String(value));
    }
  });

  return searchParams.toString() ? `${toolUrl}?${searchParams.toString()}` : toolUrl;
};

export const copyToClipboard = async (text: string): Promise<boolean> => {
  try {
    if (navigator.clipboard && window.isSecureContext) {
      await navigator.clipboard.writeText(text);
      return true;
    } else {
      // Fallback for older browsers
      const textArea = document.createElement('textarea');
      textArea.value = text;
      textArea.style.position = 'fixed';
      textArea.style.left = '-999999px';
      textArea.style.top = '-999999px';
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      const success = document.execCommand('copy');
      document.body.removeChild(textArea);
      return success;
    }
  } catch (error) {
    console.error('Failed to copy to clipboard:', error);
    return false;
  }
};

